cfg:SetSQLConsumetael("CREATE TABLE IF NOT EXISTS `consume_tael_%u_%u`( `server_id` int(10) unsigned NOT NULL, `player_id` bigint(20) unsigned NOT NULL, `item_id` int(10) unsigned NOT NULL, `consume_type` int(10) unsigned NOT NULL, `item_num` int(10) unsigned NOT NULL, `expenditure` int(10) unsigned NOT NULL, `consume_time` int(10) unsigned NOT NULL,INDEX server_player (`server_id`, `player_id`), INDEX server_player_time (`server_id`, `player_id`, `consume_time`),INDEX server_player_item (`server_id`, `player_id`, `item_id`),INDEX server_player_type (`server_id`, `player_id`, `consume_type`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;");
cfg:SetSQLItemCourses("CREATE TABLE IF NOT EXISTS `item_courses_%u_%u` (`server_id` int(10) unsigned NOT NULL,`player_id` bigint(20) unsigned NOT NULL,`item_id` int(10) unsigned NOT NULL,`item_num` int(10) unsigned NOT NULL,`from_to` tinyint(3) unsigned NOT NULL,`happened_time` int(10) unsigned NOT NULL,INDEX server_player (`server_id`, `player_id`), INDEX server_player_time (`server_id`, `player_id`, `happened_time`),INDEX server_player_item (`server_id`, `player_id`, `item_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;");
cfg:SetSQLItemHistories("CREATE TABLE IF NOT EXISTS `item_histories_%u_%u` (  `server_id` int(10) unsigned NOT NULL,`player_id` bigint(20) unsigned NOT NULL,`item_id` int(10) unsigned NOT NULL,`item_num` int(10) unsigned NOT NULL,`use_time` int(10) unsigned NOT NULL,INDEX server_player (`server_id`, `player_id`),INDEX server_player_time (`server_id`, `player_id`, `use_time`),INDEX server_player_item (`server_id`, `player_id`, `item_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;");
